mapper.select:
select
from u_task_have where 1=1
and member_id = #{memberId}
and current_state = #{currentState}
and is_valid=1 order by task_level desc

select distinct task_id,count(id) as task_count from u_task_have where 1=1
and member_id = #{memberId}
and is_valid=1 and current_state=1 order by task_id desc

select
from u_task_have where 1=1 and is_valid=1
and member_id = #{memberId}
and current_state = #{currentState}
order by task_level desc
